				if (Seed.items.i241 && Seed.items.i241>0) item241 = '<a onclick="ptBoostKnight(this,'+Cities.cities[numcid].id+','+knight.knightId+',\'i241\')"><img height=20 style="vertical-align:text-top;" src="'+GlovesImage+'" title="'+uW.itemlist.i241.name+' (Res:25%) ('+Seed.items.i241+')"></a>';
				m += '<img style="vertical-align:text-top;" width=20 src="'+kimg+'"><a title="'+tx('Assign Role')+'" onclick="pthideMe();citysel_click(document.getElementById(\'citysel_' + (numcid + 1) + '\'));setTimeout (function (){ assign_role_modal(' + knight.knightId + ');return false;}, 500);">' + knight.knightName + '</td>';
