			n += '<td class=xtab style="padding-right:2px"><a onClick="speedupCraft('+cityId+', '+item+', '+qitem+')"><img height=18 style="opacity:0.8;vertical-align:text-top;" src="'+IMGURL+'items/70/'+item+'.jpg" title="'+uW.itemlist["i"+item].name+' ('+hint+') ('+count+')"></a></td>';
