			cityId = Cities.cities[i].id;

			var str = '';
			if (t.citydelay[citynum] > 0) { str = tx('Waiting')+'...'; }
			if (Options.BuildOptions && Options.BuildOptions.AscensionReady[citynum]) { str = tx('Ascension')+'...'; }

