				Speedups += t.paintSpeedup(cityId,qcon[0][2],0,2,t.Knight,'15 mins');
				Speedups += t.paintSpeedup(cityId,qcon[0][2],0,3,t.Guinevere,'60 mins');
				Speedups += t.paintSpeedup(cityId,qcon[0][2],0,4,t.Morgana,'150 mins');
				Speedups += t.paintSpeedup(cityId,qcon[0][2],0,5,t.Arthur,'8 hours');
				Speedups += '</tr><tr>';
				Speedups += t.paintSpeedup(cityId,qcon[0][2],0,6,t.Merlin,'15 hours');
				Speedups += t.paintSpeedup(cityId,qcon[0][2],0,7,t.Divine,'24 hours');
				Speedups += t.paintSpeedup(cityId,qcon[0][2],0,8,t.Epic,'60 hours');
				Speedups += t.paintSpeedup(cityId,qcon[0][2],0,10,t.Legendary,'96 hours');

				if (Speedups != "") Speedups = '<tr><td style="padding-right:0px;padding-bottom:2px;"><table align=left cellspacing=0 cellpadding=0><tr>' + Speedups + '</tr></table></td></tr>';
				str = str+Speedups+'<tr><td class=xtab><table align=center cellspacing=0 cellpadding=0><tr><td class=xtab style="padding-right:0px;"><a class="inlineButton button14" onClick="cancelConstruction('+cityId+',0)"><span>'+tx("Cancel")+'</span></a></td></tr></table>';

