					Speedups += t.paintSpeedup(cityId,u[0],1,2,t.Knight,'15 mins');
					Speedups += t.paintSpeedup(cityId,u[0],1,3,t.Guinevere,'60 mins');
					Speedups += t.paintSpeedup(cityId,u[0],1,4,t.Morgana,'150 mins');
					Speedups += t.paintSpeedup(cityId,u[0],1,5,t.Arthur,'8 hours');
